{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyNh0VsNzgCIYFx6C8/2FMWN"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"p3sHHal6djGH"},"outputs":[],"source":["#200901082 M.JAMEEL KHALID\n","#importing libraries \n","from lxml import etree\n","import pandas as pd\n","\n","#reading xml file\n","tree = etree.parse(\"compiler.xml\")\n","root = tree.getroot()\n","bookinfo=[]\n","# extracting data from xml file\n","for book in root.findall(\"book\"):\n","    book_dict = {}\n","    book_dict['Book ID'] = book.get('id')\n","    book_dict['Author'] = book.find('author').text\n","    book_dict['Title'] = book.find('title').text\n","    book_dict['Genre'] = book.find('genre').text\n","    book_dict['Price'] = book.find('price').text\n","    book_dict['Publish Date'] = book.find('publish_date').text\n","    book_dict['Description'] = book.find('description').text\n","    bookinfo.append(book_dict)\n","\n","#creeating a data frame to write it on excel\n","data = pd.DataFrame(bookinfo)\n","print(data)\n","#writing to excel\n","data.to_excel('200901082_assign_3.xlsx', index=False)\n"]}]}